import React, { useEffect, useState } from 'react';
import { Pie } from 'react-chartjs-2';
import { Bar } from 'react-chartjs-2';

const Dashboard = ({ userEmail }) => {
  const [dashboardData, setDashboardData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`/api/dashboard?email=${userEmail}`);
        const data = await response.json();
        setDashboardData(data);
      } catch (error) {
        console.error("Error fetching dashboard data:", error);
      }
    };
    fetchData();
  }, [userEmail]);

  if (!dashboardData) return <div className="loading">Loading...</div>;

  // Chart data configurations
  const pieData = {
    labels: ['Income', 'Expenses'],
    datasets: [{
      data: [dashboardData.totalIncome, dashboardData.totalExpenses],
      backgroundColor: ['#4CAF50', '#F44336']
    }]
  };

  const barData = {
    labels: Object.keys(dashboardData.categorySpending),
    datasets: [{
      label: 'Spending by Category',
      data: Object.values(dashboardData.categorySpending),
      backgroundColor: '#2196F3'
    }]
  };

  return (
    <div className="dashboard-container">
      <div className="summary-cards">
        <div className="summary-card total-balance">
          <h3>Total Balance</h3>
          <div className="amount">${dashboardData.balance}</div>
        </div>
        
        <div className="summary-card income-card">
          <h3>Total Income</h3>
          <div className="amount">${dashboardData.totalIncome}</div>
        </div>
        
        <div className="summary-card expense-card">
          <h3>Total Expenses</h3>
          <div className="amount">${dashboardData.totalExpenses}</div>
        </div>
      </div>

      <div className="charts-section">
        <div className="chart-card">
          <h4>Income vs Expenses</h4>
          <Pie data={pieData} />
        </div>
        
        <div className="chart-card">
          <h4>Spending by Category</h4>
          <Bar data={barData} options={{ responsive: true }} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
